draw(C--E,dashed);
draw(A--M);
draw(M--E,dashed);
draw(B--N);
draw(N--E,dashed);
draw(D--Q);
draw(Q--E,dashed);
draw(Ap--Bp--Cp--Dp--cycle);
draw(Ap--M);
