dot(dots);

draw(A--B--C--cycle);
draw(D--B);

label("$A$",A,SW);
label("$B$",B,SE);
label("$C$",C,NE);
label("$D$",D,NW);
label("$6\sqrt{2}$",(A+B)/2,S);
label("$6$",(A+D)/2,NW);
label("$6$",(B+D)/2,NE);
label("$6\sqrt{3}$",(C+D)/2,NW);
label("$6\sqrt{3}$",(C+D)/2,NW);
label("$12$",(C+B)/2,E);[/asy]

