draw(O--3*I, Arrow3(6));
draw(O--3*J, Arrow3(6));
draw(O--3*K, Arrow3(6));
draw(O--A,Arrow3(6));
draw(O--B,Arrow3(6));
draw(O--V,Arrow3(6));
draw(O--5*V,dashed,Arrow3(6));
draw(A--5*V,dashed);

