Q = (0,-2/3);
F = 2*Q - D;
draw(P--D--EE--F--D);
draw(EE--Q);
label("$A$",D,W);
label("$D$",P,NE);
label("$E$",Q,SW);
label("$B$",EE,N);
label("$C$",F,SE);
