draw((2,0)--(5,3)--(0,3)--(6,0)--cycle,linewidth(0.7));
label("$A$",(0,3),W);
label("$B$",(6,0),E);
label("$D$",(2,0),S);
label("$E$",(3.4,1.3),N);
dot((3.4,1.3));
